import React, { useState, useEffect, useLayoutEffect } from 'react';
import swal from '@sweetalert/with-react'
import api from '../../../services/api';

import './style.css';
import GlobalStyle from '../../../global/styles';
import { App, Container, Content, Label } from './styles';

import { Link, useHistory } from 'react-router-dom';
import { useKidContext } from '../../../context/kidContext';
import { useTaskContext } from '../../../context/taskContext';

function Task3() {
  const {kid_name} = useKidContext();
  const {task_id} = useTaskContext();
  const [task, setTask] = useState([]);
  const [reward, setReward] = useState([]);
  var [photo, setPhoto] = useState();
  var [message, setMessage] = useState();

  const [time, setTime] = useState(0);
  const [response, setResponse] = useState();
  const [tries, setTries] = useState(1);
  const [status, setStatus] = useState('realizada');
  
  const [isActive, setIsActive] = useState(true);
  const history = useHistory();

  useLayoutEffect(() => {
    api.get(`/${kid_name}/tasks/taskListOne/${task_id}`)
    .then((response) => {
      setTask([response.data]);
    }).catch((error) => {
        console.error('error', error) 
    });
  }, []);

  useEffect(() => {
    let interval = null;
    if(isActive){
      interval = setInterval(() => {
        setTime(time => time + 1);
      }, 1000);
    } else if(!isActive && time !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, time]);

  useEffect(() => {
    api.get(`/tasks/${task_id}/listRewards`)
    .then((response) => {
      setReward([response.data]);
    }).catch((error) => {
        console.error('error', error) 
    });
  }, []);

  function montReward() {
    reward.map((reward) => {
      return (
        setPhoto([reward.photo]),
        setMessage([reward.message])
      )})
  }

  async function allowDrop(e) {
    await e.preventDefault();
  }

  async function drag(e) {
    await e.dataTransfer.setData("text", e.target.id);
  }

  async function drop(e) {
    await e.preventDefault();
    var data = e.dataTransfer.getData("text");
    e.target.appendChild(document.getElementById(data));
  }

  function stopTime() {
    setIsActive(false);
    setTime(time);
    setResponse(response);
  }

  async function create(){  

    await api.post(
        `/tasks/${task_id}/createdTaskResult`,
        { task_id, response, tries, time },
        
        //window.alert('Atividade finalizada!')
        )
        .then(response => 
        console.log(JSON.stringify({
            "task_id": task_id,
            "response": response,
            "tries": tries,
            "time": time
        })),
        console.log("Enviado!")
    );
  }

  async function updateTask() {

    await api.put(`/tasks/${task_id}/updatedTask`,
    { status }
    ).then(response => {
      console.log(response);
      console.log(JSON.stringify({
          "status": status
      }));
      console.log("Task atualizada!");
    });
  }

  const sendTask = async () => {
    await create();
    //updateTask();
    history.push('/Tasks');
  }

  return (
    <App>
      <nav className="navbar navbar-light bg-light">
        <Link to="/KidAcess">
          <button className="button button-danger">SAIR</button>
        </Link>
        <h4 className="navbar navbar-light float-center">Associando palavra a imagem</h4>
        <h1>{time} time</h1>
      </nav>
      <Container>
        <Content>
          {task.map((task) => {
            return (
              <form encType='multipar/form-data' fit-content="true" key={task.id}>
                <div className='row' style={{marginTop: '2%', justifyContent: 'center'}}>
                  <img display="center"
                    onDragOver={(e)=> allowDrop(e)} 
                    onDrop={()=> {                        
                      if( response === task.emotion ) {                        
                        stopTime();
                        montReward();
                        console.log('teste: ', photo, message)
                        swal({
                          content: (
                            <div className="pyro" >
                          
                              <div className="before"></div>
                              <div className="after"></div>
                            </div>
                          ),
                          title: message, 
                          icon: photo
                        })
                        .then(() => {
                          sendTask();
                        })
                      } else {
                        setTries(tries+1);
                        console.log('tentativas: ', tries);
                        stopTime();
                        swal({title: 'não foi desta fez', icon: "https://w7.pngwing.com/pngs/108/553/png-transparent-smiley-sadness-emoticon-crying-smiley-faces-face-smiley-sticker.png"})
                        .then(() => {
                          setIsActive(true);
                        })
                      }
                    }}
                    align="center"  
                    src={task.image} 
                  />
                </div>

                <div className="row" style={{marginTop: '5%', justifyContent: 'center'}}>
                  <Label id="emotion1" name="emotion1" style={{marginRight: '4%', border: '3px solid #f25f9c', background: '#e47096'}}
                    value={task.response1}
                    draggable={true} 
                    onDragStart={(e)=> {drag(e); setResponse(task.response1)}}
                    >{task.response1}
                  </Label>

                  <Label id="emotion2" name="emotion2" style={{marginRight: '4%', border: '3px solid #10d1f3', background: '#70cde4'}}
                    value={task.response2} 
                    draggable={true} 
                    onDragStart={(e)=> {drag(e); setResponse(task.response2)}}
                    >{task.response2}
                  </Label>

                  <Label id="emotion3" name="emotion3" style={{border: '3px solid #e3c106', background: '#f3ed12'}}
                    value={task.response3}
                    draggable={true} 
                    onDragStart={(e)=> {drag(e); setResponse(task.response3)}}
                    >{task.response3}
                  </Label>
                </div>
              </form>
            )
          })}

          <div className="row" style={{marginTop: '5%', justifyContent: 'right'}}>
            <button className="button button-save"
              onClick={(e) => {
                swal({
                  title: 'Tem certeza que deseja finalizar a tarefa?',
                  text: 'Uma vez finalizada, não poderá refaze-la',
                  icon: 'warning',
                  buttons: ["Recomeçar", "Finalizar"]
                }).then((willFinalizar) => {
                  if(willFinalizar) {
                    swal('Resultados salvos', {
                      icon: 'success',
                    }).then(() => {
                      e.preventDefault();
                      sendTask();
                    });
                  } else {
                    setTries(1);
                    setTime(0);
                    setIsActive(true);
                  }
                });

                stopTime();
                console.log('time: ', time);
              }}
            >CONCLUIR</button>
          </div>
        </Content>
        <GlobalStyle />
      </Container>
    </App>
  )
}

export default Task3;
